Writing and Formatting Styles:
{
   "[Writing Styles] Likes": {"1": "Conversational tone, e.g., 'Hey there!'", "2": "Simple vocabulary, e.g., 'Let's code a cool project!'", "3": "Use of emojis, e.g., '👍' or '😊'", "4": "Positive encouragement, e.g., 'You'll get the hang of it!'", "5": "Casual, relatable anecdotes, e.g., 'I once struggled with...'"}, 
   "[Writing Styles] Dislikes": {"1": "Formal tone, e.g., 'Dear esteemed colleague'", "2": "Complex jargon, e.g., 'Utilize comprehensive algorithms'", "3": "Overly critical language, e.g., 'This approach is fundamentally flawed'", "4": "Impersonal statements, e.g., 'One must consider...'", "5": "Negative framing, e.g., 'It's unlikely you'll succeed'"}, 
   "[Formatting Styles] Likes": {"1": "Clear subject lines, e.g., 'New Python Project Ideas'", "2": "Short paragraphs, e.g., 2-3 sentences max", "3": "Use bullet points for clarity, e.g., '- First task: setup environment'", "4": "Friendly sign-off, e.g., 'Cheers, [Name]'", "5": "Highlighting important points, e.g., using bold text for key terms"}, 
   "[Formatting Styles] Dislikes": {"1": "Long, dense paragraphs, e.g., containing several sentences piled together", "2": "Complicated headers, e.g., 'III. Analysis of Algorithm Efficiency'", "3": "Unnecessary formal signatures, e.g., 'Sincerely yours, [Full Name]'", "4": "Excessive use of italics, e.g., 'This is *not* recommended'", "5": "Tiny font sizes, e.g., unreadable text like 8pt"}
}
