Writing and Formatting Styles:
{
   "[Writing Styles] Likes": {
       "1": "Technical jargon appropriate for scientific literature",
       "2": "Formal tone",
       "3": "Precise and concise wording",
       "4": "Fact-based and evidence-supported content",
       "5": "Neutral valence"
   },
   "[Writing Styles] Dislikes": {
       "1": "Casual or informal tone",
       "2": "Excessive use of emojis",
       "3": "Overly emotional or subjective expressions",
       "4": "Vague or ambiguous language",
       "5": "Dramatic or sensationalist expressions"
   },
   "[Formatting Styles] Likes": {
       "1": "Subsections with clear headings",
       "2": "Proper use of citations and references",
       "3": "Abstracts and summaries",
       "4": "Consistent formatting with bullet points and lists",
       "5": "Footnotes for additional context or information"
   },
   "[Formatting Styles] Dislikes": {
       "1": "Long paragraphs without breaks",
       "2": "Lack of clear structure",
       "3": "Improper or absent citation format",
       "4": "Informal abbreviations",
       "5": "Overly decorative or artistic fonts"
   }
}
